import './index.css';
function Right({showNoteEditor, ReactQuill, useState, noteTitle, titleChange}) {



    return (
        <div className="Right">
            {showNoteEditor ? (
                <div className="editorContainer">
                    <div className="editorTop">
                        <div>
                            <h1 contentEditable="true" onBlur={titleChange}>
                                {noteTitle}
                            </h1>
                        </div>
                        <div>
                            <button>Save</button>
                            <button>Delete</button>
                        </div>
                    </div>
                    <p>Date and Time</p>
                    <div className="editorBottom">
                        <ReactQuill />
                    </div>
                </div>
            ) : (
                <p>Select a note, or create a new one</p>
            )}
        </div>


      );

}

export default Right;
