import React, { useState, useEffect } from 'react';

function Right({ showNoteEditor, ReactQuill, deleteNote, useState, currentNote }) {
  const [noteTitle, setNoteTitle] = useState('');
  const [noteContent, setNoteContent] = useState('');

  // Retrieve the notes from local storage on component mount
  useEffect(() => {
    const storedNotes = JSON.parse(localStorage.getItem('notes'));
    if (storedNotes) {
      const selectedNote = storedNotes.find((note) => note.id === currentNote);
      if (selectedNote) {
        setNoteTitle(selectedNote.title);
        setNoteContent(selectedNote.content);
      }
    }
  }, [currentNote]);

  const saveNote = () => {
    // Save the note to local storage
    const storedNotes = JSON.parse(localStorage.getItem('notes')) || [];
    const updatedNotes = storedNotes.map((note) => {
      if (note.id === currentNote) {
        return {
          ...note,
          title: noteTitle,
          content: noteContent,
        };
      }
      return note;
    });
    localStorage.setItem('notes', JSON.stringify(updatedNotes));
  };

  return (
    <div className="Right">
      {showNoteEditor ? (
        <div className="editorContainer">
          <div className="editorTop">
            <div>
              <input id="notetitle" type="text" value={noteTitle} onChange={(e) => setNoteTitle(e.target.value)} />
            </div>
            <div>
              <button onClick={saveNote}>Save</button>
              <button onClick={deleteNote}>Delete</button>
            </div>
          </div>
          <p>Date and Time</p>
          <div className="editorBottom">
            <ReactQuill
              className="editor"
              value={noteContent}
              onChange={(value) => setNoteContent(value)}
            />
          </div>
        </div>
      ) : (
        <p id="empty">Select a note, or create a new one</p>
      )}
    </div>
  );
}

export default Right;
