import './index.css';
function Right({showNoteEditor, ReactQuill, deleteNote, useState, currentNote,notes}) {

    const [noteTitle, setNoteTitle] = useState('Untitled');

    const saveNote = () => {
        console.log("Save Button Clicked");
        console.log(currentNote);
        for(let i = 0; i < notes.length; i++){
          if(notes[i].id === currentNote){
            notes[i].title = noteTitle;
            console.log("Saved Title:",notes[i].title);
            notes[i].content = document.getElementsByClassName("ql-editor")[0].innerHTML;
            console.log("Saved Content:",notes[i].content);
          }
        }

        localStorage.setItem('notes', JSON.stringify(notes));
      }



    return (
        <div className="Right">
            {showNoteEditor ? (
                <div className="editorContainer">
                    <div className="editorTop">
                        <div>
                        <input id = "notetitle" type="text" value={noteTitle} onChange={(e) => setNoteTitle(e.target.value)} />
                        </div>
                        <div>
                            <button onClick = {saveNote}>Save</button>
                            <button onClick={deleteNote}>Delete</button>
                        </div>
                    </div>
                    <p>Date and Time</p>
                    <div className="editorBottom">
                    <ReactQuill className="editor" />
                    </div>
                </div>
            ) : (
                <p id = "empty">Select a note, or create a new one</p>
            )}
        </div>


      );

}

export default Right;
