import "./index.css";

import Left from "./Left";
import Right from "./Right";
import React, { useState } from 'react';
import { useEffect, useRef } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { v4 as uuidv4 } from 'uuid';
import { UNSAFE_NavigationContext, useParams } from "react-router-dom";

import { BrowserRouter, Routes, Route, Link } from "react-router-dom";




function App() {

  //show no notes
  const [showNoNotes, setShowNoNotes] = useState(true);



  //side Bar button toggle
  const[sideBar, setSideBar] = useState(true);

  const toggleSideBar = () => {
    console.log("Side Bar Button Clicked");
    setSideBar(!sideBar);
  };

  //add note function
  const [notes, setNotes] = useState([]);
  const [showNoteEditor, setShowNoteEditor] = useState(false);
  const [notePage, setNotePage] = useState(notes.length+1);

  const [currentNote, setCurrentNote] = useState(null);
  const addNote = () => {
    console.log("Add Note Button Clicked");
    setShowNoNotes(false);

    const newNote = {
      noteNum: notePage,
      id: uuidv4(),
      title: "Untitled",
      content: "...",
      //lastModified: Date.now(),
    };

    //increment note id
    setNotePage(notePage + 1);

    //add new note to notes array
    setNotes([...notes, newNote]);

    //create editor for new node
    setShowNoteEditor(true);

    //set the current note
    setCurrentNote(newNote.id);    
  };

  //delete note function
  const deleteNote = () =>{
    //if no notes, return
    if(notes.length === 0){
      return
    }
    const lastNoteId = notes[notes.length - 1].id;
    console.log("Delete Button Clicked");
    console.log(lastNoteId);
    //if id of note to delete matches id of note in notes array, delete note
    setNotes(notes.filter((note) => note.id !== lastNoteId));

    //decrement note id
    setNotePage(notePage - 1);

    if(notes.length === 1){
      setShowNoteEditor(false);
      setShowNoNotes(true);
    }
  }

  //select note function





  return (


    <div className="App">

      <div className = "Top">
        <button onClick={toggleSideBar}>â˜°</button>
        <div id = "title">
          <h1>Lotion</h1>
          <p>Like Notion, but worse.</p>
        </div>
        <div></div>
      </div>

      <div className = "Bot">

        {sideBar && (
          <div id="Left">
            <Left notes={notes} addNote={addNote} showNoNotes={notes.length === 0} />
          </div>
        )}

        <div id = "Right">
            <Right showNoteEditor = {showNoteEditor} ReactQuill={ReactQuill} useState = {useState} 
            deleteNote = {deleteNote} notes = {notes} currentNote={currentNote} showNoNotes={showNoNotes}/>
        </div>
      </div>

    </div>


  );
}




export default App;
