import './index.css';
import { useState, useEffect } from 'react';

function Right({showNoteEditor, ReactQuill, deleteNote, notes, selectedNote}) {
    const [noteTitle, setNoteTitle] = useState('Untitled');
    const [noteContent, setNoteContent] = useState('');

    useEffect(() => {
        if (selectedNote) {
            const note = notes.find(note => note.id === selectedNote);
            setNoteTitle(note.title);
            setNoteContent(note.content);
        }
    }, [selectedNote, notes]);

    const titleChange = (event) => {
        setNoteTitle(event.target.value);
    };

    const contentChange = (content) => {
        setNoteContent(content);
    };

    const saveNote = () => {
        const noteIndex = notes.findIndex(note => note.id === selectedNote);
        const updatedNotes = [...notes];
        updatedNotes[noteIndex] = {
            id: selectedNote,
            title: noteTitle,
            content: noteContent
        };
        localStorage.setItem('notes', JSON.stringify(updatedNotes));
    };

    return (
        <div className="Right">
            {showNoteEditor ? (
                <div className="editorContainer">
                    <div className="editorTop">
                        <div>
                            <input id="notetitle" type="text" value={noteTitle} onChange={titleChange} />
                        </div>
                        <div>
                            <button onClick={saveNote}>Save</button>
                            <button onClick={deleteNote}>Delete</button>
                        </div>
                    </div>
                    <p>Date and Time</p>
                    <div className="editorBottom">
                        <ReactQuill className="editor" value={noteContent} onChange={contentChange} />
                    </div>
                </div>
            ) : (
                <p id="empty">Select a note, or create a new one</p>
            )}
        </div>
    );
}

export default Right;
