import './index.css';
function Right({showNoteEditor, ReactQuill, useState}) {

    const [noteTitle, setNoteTitle] = useState('Untitled');

    const titleChange = (event) => {
        setNoteTitle(event.target.innerText);
    };

    return (
        <div className="Right">
            {showNoteEditor ? (
                <div className="editorContainer">
                    <div className="editorTop">
                        <h1 contentEditable="true" onBlur={titleChange}>
                            {noteTitle}
                        </h1>
                        <p>Date and Time</p>
                    </div>
                    <div className="editorBottom">
                    <ReactQuill />
                    </div>
                </div>
            ) : (
                <p>Select a note, or create a new one</p>
            )}
        </div>


      );

}

export default Right;
