import './index.css';
function Right({showNoNotes, showNoteEditor, ReactQuill}) {
    return (
        <div className="Right">
            {showNoteEditor ? (
                <div className="editorContainer">
                    <input type="text" placeholder="Enter Title" />
                    <ReactQuill />
                </div>
            ) : (
                <p>Select a note, or create a new one</p>
            )}
        </div>


      );

}

export default Right;
