import './index.css';
import React, { useState, useEffect } from 'react';
function Left({notes, addNote, showNoNotes}) {

    const [selectedNote, setSelectedNote] = useState(null);

    const selectNote = () => {
        console.log("Note Clicked");
        setSelectedNote(notes.noteNum);
        console.log("this is selected",selectedNote);


    }





    return (
        <div className="Left">
            <div className="LeftTop">
                <h1>Notes</h1>
                <button onClick = {addNote}>+</button>
            </div>
            <div className="NotesContainer">
                {showNoNotes &&
                 <p>No Notes Yet</p>}
            {notes.map((note) => (
                <div onClick ={selectNote}className='Note' key={note.id}>
                    <div className='NoteTitle'>
                        <h2>{note.title}</h2>
                        <p>{note.content && note.content.substr(0,100)+ "..."}</p>
                        <p>{note.id}</p>
                        <p>{note.noteNum}</p>
                    </div>
                </div>
            ))}

            </div>
        </div>
    );
}

export default Left;
