import './index.css';
import { useEffect, useState } from 'react';

function Right({ showNoteEditor, ReactQuill, deleteNote, selectedNote, notes, updateNote }) {
  const [noteTitle, setNoteTitle] = useState('Untitled');
  const [noteContent, setNoteContent] = useState('');

  useEffect(() => {
    if (selectedNote) {
      const selectedNoteData = notes.find((note) => note.id === selectedNote);
      setNoteTitle(selectedNoteData.title || 'Untitled');
      setNoteContent(selectedNoteData.content || '');
    }
  }, [selectedNote, notes]);

  const handleTitleChange = (event) => {
    setNoteTitle(event.target.value);
  };

  const handleSaveNote = () => {
    const updatedNote = {
      id: selectedNote,
      title: noteTitle,
      content: noteContent,
      updated: new Date(),
    };
    updateNote && updateNote(updatedNote);
    localStorage.setItem(selectedNote, JSON.stringify(updatedNote));
  };

  const handleDeleteNote = () => {
    deleteNote && deleteNote();
    localStorage.removeItem(selectedNote);
  };

  return (
    <div className="Right">
      {showNoteEditor ? (
        <div className="editorContainer">
          <div className="editorTop">
            <div>
              <input id="notetitle" type="text" value={noteTitle} onChange={handleTitleChange} />
            </div>
            <div>
              <button onClick={handleSaveNote}>Save</button>
              <button onClick={handleDeleteNote}>Delete</button>
            </div>
          </div>
          <p>Date and Time</p>
          <div className="editorBottom">
            <ReactQuill className="editor" value={noteContent} onChange={setNoteContent} />
          </div>
        </div>
      ) : (
        <p id="empty">Select a note, or create a new one</p>
      )}
    </div>
  );
}

export default Right;
