import './index.css';
function Left({notes, addNote, showNoNotes}) {

    const seletNote = () => {
        console.log("Note Clicked");
    }






    return (
        <div className="Left">
            <div className="LeftTop">
                <h1>Notes</h1>
                <button onClick = {addNote}>+</button>
            </div>
            <div className="NotesContainer">
                {showNoNotes &&
                 <p>No Notes Yet</p>}
            {notes.map((note) => (
                <div onClick = {seletNote}className='Note' key={note.id}>
                    <div className='NoteTitle'>
                        <h2>{note.title}</h2>
                        <p>{note.content && note.content.substr(0,100)+ "..."}</p>
                        <p>{note.id}</p>
                        <p>{note.noteNum}</p>
                    </div>
                </div>
            ))}

            </div>
        </div>
    );
}

export default Left;
