import "./index.css";
import Left from "./Left";
import Right from "./Right";
import React, { useState } from 'react';
import { useEffect, useRef } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { v4 as uuidv4 } from 'uuid';



function App() {
  //side Bar button toggle
  const[sideBar, setSideBar] = useState(true);

  const toggleSideBar = () => {
    console.log("Side Bar Button Clicked");
    setSideBar(!sideBar);
  };

  //add note function
  const [notes, setNotes] = useState([]);
  const [showNoteEditor, setShowNoteEditor] = useState(false);

  const [currentNote, setCurrentNote] = useState(null);

  const addNote = () => {
    console.log("Add Note Button Clicked");

    const newNote = {
      id: uuidv4(),
      title: "Untitled",
      content: "...",
      //lastModified: Date.now(),
    };

    //add new note to notes array
    setNotes([...notes, newNote]);

    //create editor for new node
    setShowNoteEditor(true);

    //set the current note
    setCurrentNote(newNote.id);


  };


  //delete note function
  const deleteNote = () =>{
    //if no notes, return
    if(notes.length === 0){
      showNoteEditor = false;
      return;
    }
    const lastNoteId = notes[notes.length - 1].id;
    console.log("Delete Button Clicked");
    console.log(lastNoteId);
    //if id of note to delete matches id of note in notes array, delete note
    setNotes(notes.filter((note) => note.id !== lastNoteId));

  }



  return (
    <div className="App">

      <div className = "Top">
        <button onClick={toggleSideBar}>â˜°</button>
        <div id = "title">
          <h1>Lotion</h1>
          <p>Like Notion, but worse.</p>
        </div>
        <div></div>
      </div>

      <div className = "Bot">

        {sideBar && (
          <div id="Left">
            <Left notes = {notes} addNote={addNote}/>
          </div>
        )}

        <div id = "Right">
            <Right showNoteEditor = {showNoteEditor} ReactQuill={ReactQuill} useState = {useState} 
            deleteNote = {deleteNote} notes = {notes} currentNote={currentNote}/>
        </div>
      </div>

    </div>


  );
}

export default App;
