import './index.css';
import React, { useState, useEffect } from 'react';
function Left({notes, addNote, showNoNotes}) {

    const [selectedNote, setSelectedNote] = useState(0);

    useEffect(() => {

        for(let i=1;i<notes.length+1;i++){
            let btn = document.getElementById(i);
            console.log(btn);
            if(btn){
                btn.addEventListener("click", () => {
                    console.log("clicked");
                    setSelectedNote(i);
                })
            }
        }


    },[notes])





    return (
        <div className="Left">
            <div className="LeftTop">
                <h1>Notes</h1>
                <button onClick = {addNote}>+</button>
            </div>
            <div className="NotesContainer">
                {showNoNotes &&
                 <p>No Notes Yet</p>}
            {notes.map((note) => (
                <div className='Note' key={note.id} id={note.noteNum}>
                    <div className='NoteTitle'>
                        <h2>{note.title}</h2>
                        <p>{note.content && note.content.substr(0,100)+ "..."}</p>
                        <p>{note.id}</p>
                        <p>{note.noteNum}</p>
                    </div>
                </div>
            ))}

            </div>
        </div>
    );
}

export default Left;
