import "./index.css";
import Left from "./Left";
import Right from "./Right";
import { useState } from "react";

function App() {
  const[sideBar, setSideBar] = useState(false);

  const toggleContent = () => {
    console.log("Show Side Bar Button Clicked");
    setSideBar(!sideBar);
  };




  return (
    <div className="App">

      <div className = "Top">
        <button onClick={toggleContent}>+</button>
        <div id = "title">
          <h1>Lotion</h1>
          <p>Like Notion, but worse.</p>
        </div>
        <div></div>
      </div>

      <div className = "Bot">

      <Left />
        {sideBar && (
          <div id="Left">
            <Left />
          </div>
        )}

        <div id = "Right">
            <Right />
        </div>
      </div>

    </div>


  );
}

export default App;
