import './index.css';
import React, { useState, useEffect } from 'react';
function Left({notes, addNote, showNoNotes, selectedNote, setSelectedNote}) {

    //const [selectedNote, setSelectedNote] = useState(0);


    //when notes is created , trigger this
    useEffect(() => {
        // loop through all notes
        for (let i = 1; i < notes.length +1; i++) {
          const currentNote = document.getElementById(i);
          // check if note exists
          if (currentNote) {
            // add event listener to note
            currentNote.addEventListener("click", () => {
              console.log("Note clicked: ", notes[i-1].id);
              setSelectedNote(notes[i-1].id);
            });
          }
        }
      }, [notes]);


    //when a note is clicked, change the color of the note
    function changeNote() {
        for (let i = 1; i <= notes.length; i++) {
          let currentNote = document.getElementById(i);
          if (currentNote) {
            console.log("CHANGE NOTE FUNC: ", notes[i - 1].id);
            if (notes[i - 1].id === selectedNote) {
              currentNote.style.backgroundColor = "#423c52";
              currentNote.style.color = "white";
            } else {
              currentNote.style.backgroundColor = "white";
              currentNote.style.color = "black";
            }
          }
        }
      }



    changeNote();



    return (
        <div className="Left">
            <div className="LeftTop">
                <h1>Notes</h1>
                <button onClick = {addNote}>+</button>
            </div>
            <div className="NotesContainer">
                {showNoNotes &&
                 <p>No Notes Yet</p>}
            {notes.map((note) => (
                <div className='Note' key={note.id} id={note.noteNum}>
                    <div className='NoteTitle'>
                        <h2>{note.title}</h2>
                        <p>{note.content && note.content.substr(0,100)+ "..."}</p>
                        <p>{note.id}</p>
                        <p>{note.noteNum}</p>
                    </div>
                </div>
            ))}

            </div>
        </div>
    );
}

export default Left;
